"use strict";

/**
 * constructor
 */

var DataUtil = {
  set:function(id,value){
    this._dataHash = this._dataHash || {};
    this._dataHash[id] = value;
  },
  get:function(id){
    this._dataHash = this._dataHash || {};
    return this._dataHash[id];
  },
  has:function(id){
    this._dataHash = this._dataHash || {};
    return !!this._dataHash[id];
  },
  all:function(){
    this._dataHash = this._dataHash || {};
    return this._dataHash;
  },
  remove:function(id){
    this._dataHash = this._dataHash || {};
    if(this._dataHash[id]){
      delete this._dataHash[id];
    }
  }
};

module.exports = DataUtil;

